import axios from 'axios';

const API_URL = 'http://localhost:1337'; // URL de tu Strapi

const getCampaigns = async () => {
    try {
      const response = await axios.get('http://localhost:1337/api/campaigns');
      return response.data; // Devuelve los datos de las campañas
    } catch (error) {
      console.error('Error fetching campaigns:', error);
      throw error; // Lanza el error para manejarlo en otro lugar
    }
  };

export const createDonation = async (donationData) => {
  try {
    const response = await axios.post(`${API_URL}/src/api/donations`, donationData);
    return response.data;
  } catch (error) {
    console.error('Error creating donation:', error);
    throw error;
  }
};
