import React, { useEffect, useState } from 'react';
import { getCampaigns } from '../services/api';

const CampaignList = () => {
    const [campaigns, setCampaigns] = useState([]);
    const campaignArray = campaigns.data || [];

    useEffect(() => {
        const fetchCampaigns = async () => {
            const data = await getCampaigns();
            console.log('Datos de campañas:', data);
            setCampaigns(data);
        };
        fetchCampaigns();
    }, []);

    return (
        <div>
            <h1>Campañas</h1>
            {/* <ul>
                {campaigns.map(campaign => (
                    <li key={campaign.id}>{campaign.title}</li>
                ))}
            </ul> */}
            <h1>Lista de Campañas</h1>
            {campaignArray.length === 0 ? (
                <p>No hay campañas disponibles.</p>
            ) : (
                campaignArray.map((campaign) => (
                    <div key={campaign.id}>
                        <h2>{campaign.title}</h2>
                        <p>{campaign.attributes.description}</p>
                    </div>
                ))
            )}
        </div>
    );
};

export default CampaignList;
